// 表单输入框 模拟placeholder
var ns = ns || {};
ns.placeholder = function (_form) { 
    var _input = document.createElement('input');
    // 判断是否支持H5 placeholder属性
    if (typeof _input.placeholder !== 'undefined') return;
    $(_form).find('.ie-placeholder').each(function () {
        var _label = $(this)
        , _input = _label.next('input[placeholder]');
        
        if ($.trim(_input.val()).length == 0) _label.show();
        _input.on('keyup keydown focus', function () {
                var val = $.trim(this.value);
                _label[val ? 'hide' : 'show']();
            })
            .on('blur', function () {
                var val = $.trim(this.value);
                if (!val) _label.show();
            })
            .trigger('keyup');
    });
};
